import React from "react"

class DetailServices extends React.Component {
    constructor() {
        super()
        this.state = {
            loading: true,
            services: []
        }

    }

    componentDidMount(props) {
        console.log("DetailServices Mounted", this.props.services)
        this.setState({
            loading:false,
            services: this.props.services
        })
    }

   render() {
    console.log("DetailServicesState", this.state)
    var serviceIcon = this.state.services.map(service => (
        <button className="button is-outline is-primary">{service.serviceName}</button>
    ))

    return (
        <div>
        <h1>DetailsServices</h1> 
        {serviceIcon}
        </div>   )
   }
}


export default DetailServices
