  departure.concat(abfahrtDatum.getDate())
  
 
  var abfahrtJahr = abfahrtDatum.getFullYear()
  var abfahrtMonat = abfahrtDatum.getMonth()+1
  var abfahrtTag = abfahrtDatum.getDate()

  var returnDatum = new Date(props.return)
  var returnJahr = returnDatum.getFullYear()
  var returnMonat = returnDatum.getMonth()+1
  var returnTag = returnDatum.getDate()

    console.log("Abfahrt", departure)
    
      console.log("return", returnTag, returnMonat, returnJahr)
  return (
    <div className="column is-one-fifth">
      <div className="card trip">
        <div className="card-image has-ribbon">
        <div className="ribbon is-medium is-danger">Nur noch wenige Pl√§tze</div>
          <figure className="image">
            <img
              src={props.hero}
              alt="Placeholder image"
            />
          </figure>
        </div>
        <div className="card-content">
          <div className="media">
            <div className="media-left">
              <FaSuitcase />
            </div>
            <div className="media-content">
              <p className="title is-4"><Link to={`/reisen/${props.id}`}>{props.title}</Link></p>
              <p className="subtitle is-6"></p>
            </div>
          </div>

          <div className="content">
            {props.desc}
            <br />
            <time dateTime="01.01.2016">{props.departure} - {props.return}</time>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TripCard;
