import React from"react";
import Strapi from "strapi-sdk-javascript";
import setToken from "../utils/"
import PopUp from "../utils/PopUp"

class Register extends React.Component {
    constructor() {
        super()

        this.state = {
           email: "",
           vorname: "",
           nachname:"",
           password:"",
           passwordRepeat:"",
           loading: false,
           popup: false,
           popUpMessage:""
        }

        this.handleChange = this.handleChange.bind(this)
    }


    handleChange(event) {
        event.persist();
        const {name, value} = event.target

        this.setState({
            [name] : value
        })
    }

    handleSubmit = async event => {
        event.preventDefault();
        const { email, vorname, nachname, password} = this.state;

        if (this.isFormEmpty(this.state)) {
            console.log("Formular leer")
            return;
        }

        //Sign up User
        try {
            this.setState({loading:true})
            const response = await Strapi.register( email, password)
            this.setState({ loading: false})
            setToken(response.jwt)
            //console..log("TOKEN", response.jwt)
            this.redirectUser("/shopping-cart")
        } catch (err) {
            this.setState({loading: false})
            console.log("Register klappt nicht", err.message)
        }
    }

    isFormEmpty = ({ username, email, password }) => {
        return !username || !email || !password;
      };

      showPopUp = PopUp => {
        this.setState({ popup: true, popupMessage });
        setTimeout(() => this.setState({ popup: false, popupMessage: "" }), 5000);
      };

    render() {
        const { popupMessage, toast, loading } = this.state;
    return(
        <div>
            <div className="box">
            <p>
            <b>Sie haben noch kein Kundenkonto?</b>
            </p>
            <p className="is-size-7">Dann eröffnen Sie einfach eines. Füllen Sie dazu bitte das folgende Formular aus.</p>
            <form className="has-text-left">
                    <div className="field">
                    <label className="label is-small has-text-left">Ihr Vorname</label>
                              <div className="control">
                                    <input 
                                        className="input is-primary is-small"
                                        type="vorname" 
                                        name="vorname" 
                                        id="vorname"
                                        placeholder="Vorname" 
                                        value={this.state.vorname}
                                        onChange={this.handleChange}
                                    />
                                </div>
                                </div>

                    <div className="field">
                    <label className="label is-small has-text-left">Ihr Nachname</label>
                                <div className="control">
                                    <input 
                                        className="input is-primary is-small"
                                        type="nachname" 
                                        name="nachname" 
                                        id="nachname"
                                        placeholder="Nachname" 
                                        value={this.state.nachname}
                                        onChange={this.handleChange}
                                    />
                                </div>
                                </div>
                    <div className="field">
                    <label className="label is-small has-text-left">Ihre EMail-Adresse</label>

                                <div className="control">
                                    <input 
                                        className="input is-primary is-small"
                                        type="email" 
                                        name="login" 
                                        id="login"
                                        placeholder="login" 
                                        value={this.state.login}
                                        onChange={this.handleChange}
                                    />
                                </div>
                                </div>

                    <div className="field">
                    <label className="label is-small has-text-left">Ihr Passwort</label>

                                <div className="control">
                                    <input 
                                        className="input is-primary is-small"
                                        type="password" 
                                        name="password" 
                                        id="password"
                                        placeholder="password" 
                                        value={this.state.password}
                                        onChange={this.handleChange}
                                    />
                                </div>
                                </div>
                    <div className="field">
                    <label className="label is-small has-text-left">Bitte wiederholen Sie Ihr Passwort</label>

                                <div className="control">
                                    <input 
                                        className="input is-primary is-small"
                                        type="password" 
                                        name="passwordRepeat" 
                                        id="passwordRepeat"
                                        placeholder="passwordRepeat" 
                                        value={this.state.passwordRepeat}
                                        onChange={this.handleChange}
                                    />
                                </div>
                                </div>

                    <br />
                    <div className="has-text-centered">
                    <button className="button is-primary" onClick={this.checkClick}>Konto anlegen</button>
                    </div>


            </form>
            </div>
                <br/>
                <PopUp show={popup} message={popupMessage} />
        </div>
    )
}
}

export default Register
