import React from "react"
import BookingDetails from "./booking/BookingDetails"
import AccountAdress from "./account/AccountAdress"
import Companion from "./booking/Companions"
import BookingAddServices from "./booking/BookingAddServices"
import BookingButton from "./booking/BookingButton"

class MeinKonto extends React.Component {
    constructor() {
        super()
    }
    state = {
        companions: false
    }

    componentWillMount() {
       // console.log("Props", this.props)
       // console.log("MeinKontoKeys", localStorage.getItem("keys"))
       // console.log("MeinKonto", localStorage.getItem("username"))
       let localStorageKeys = []
        localStorageKeys.push(localStorage.getItem("keys").split(","))
        localStorageKeys.push(localStorage.getItem("cartKeys").split(","))
        //let temp = localStorageKeys.split(",")
       //console.log("LOCALSTORAGEKEYS", localStorageKeys)

       for (let o = 0; o < localStorageKeys.length; o++) {

        for (let i =0; i < localStorageKeys[o].length; i++) {
           // console.log("Key fÃ¼r Storage", localStorageKeys[o][i])
          //  console.log("From Storagekeys", localStorage.getItem(localStorageKeys[o][i]))
            let key = localStorageKeys[o][i]
            let value = localStorage.getItem(localStorageKeys[o][i])
            this.setState({
                [key] : value
            })

        }
    }
        // set the data in state and use it through the component
        //localStorage.clear();
        // removing the data from localStorage.  Since if user clicks for another invoice it overrides this data
     }

    componentDidMount() {
        let regex = new RegExp ("(trip_)")
        let stateKeys = []
        let bookingKeys = []
        let personalKeys =[]
        Object.keys(this.state).map((n)=> stateKeys.push(n))
        console.log("StateCheck", stateKeys)
        for (let i = 0; i < stateKeys.length; i++) {
            if (regex.test(stateKeys[i])) {
                bookingKeys.push(stateKeys[i])
            } else {
                personalKeys.push(stateKeys[i])
            }
        }

        if (this.state.trip_adults >1 || this.state.trip_kids > 0) {
            this.setState ({
                companions: true
            })

        }


    }

    render() {

      console.log("strasse", this.state.Strasse)
        return (
            <div className="container is-fluid">
                <div className="box">
                    <h1 className="title">Meine Buchung</h1>
                    <p style={{padding:"2em"}}>Hallo {this.state.username}!</p>
                    <div className="columns">
                        <div className="column is-two-thirds">
                            <h3>Meine persÃ¶nlichen Daten</h3>
                            <AccountAdress data={this.state} keys/>
                            {this.state.companions ? <Companion kids={this.state.trip_kids} adults={this.state.trip_adults}/>: ""}

                            </div>
                            {this.state.trip_id ?
                            <div>
                            <div style={{position:"relative"}} className="is-hidden-touch">
                            <div className="column box has-background-primary " style={{position:"fixed", right:"auto"}}><BookingButton /><BookingDetails /></div>
                            </div>
                            <div style={{position:"relative"}} className="is-hidden-desktop">
                            <div className="column box has-background-primary " style={{position:"fixed", right:"auto"}}><BookingButton /><BookingDetails /></div>
                            </div>
                            </div>
                            : ""}


                    </div>
                </div>
        </div>
        )
    }
}

export default MeinKonto
