import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';
const apiUrl= 'http://home.luis-backt-und-kocht.de:1337';

class DetailHeroCarousel extends React.Component{
    state = {
        loading:true
    }


    componentDidMount(){
         this.setState({
             loading:false
         })

    }

    render() {
         var carouselItem = this.props.images.map(image =>(
        <div className="carouselHeight">
                    <img src={`${apiUrl}${image}`} width="350px" height="150px" />
                        <p className="legend">
                        Legend 
                        </p>
                </div>
          ));
        return (


            <Carousel axis="vertical" useKeyboardArrows autoPlay infiniteLoop>
                {carouselItem}
            </Carousel>
        );
        }
}

export default DetailHeroCarousel
