import React from "react";
import "./App.sass";
import "./styles.css";
import Strapi from "strapi-sdk-javascript/build/main"



import NavBar from "./components/NavBar";
import TripCard from "./components/TripCard";
import Hero from "./components/Hero";
import Pagination from "./components/Pagination";
import FilterTrips from "./components/FilterTrips";
import { FaTrophy } from "react-icons/fa";

//const apiUrl= 'http://localhost:1337';
const apiUrl = `${process.env.REACT_APP_BACKEND_URL}`
const strapi = new Strapi(apiUrl);
console.log("URL", apiUrl)
console.log("strapi", strapi+'/graphql')


class App extends React.Component{
  state = {
    trips : [],
    loading: true
  }

  async componentDidMount() {
    try{
        const response = await strapi.request('POST', '/graphql', {
          data: {
            query: `query { trips {
  trip_dates {
    departure
    return
  }
  short_description
  long_description
  pics {
    name
    url
  }
  priceAdult
  priceReduced

}}`
          }

        });
        console.log("response",response.data.trips)
        this.setState({ 
          trips: response.data.reises,
          loading: false})
    } catch(err) {
      console.error(err) };
    }


  render() {
    //console.log("State", this.state.trips[0].short_description)
    const firstTrip = this.state.loading ? "Loading..." : this.state.trips[0].title;
    const firstPic = this.state.loading ? "Loading..." : this.state.trips[0].short_description;
    console.log("Short", firstPic)
    const tripCards = this.stateloading ? "Loading..." : this.state.trips.map(trip => (
      <TripCard title={trip.title} hero={`${apiUrl}${trip.trip_pics[0].url}`} desc={trip.short_description} length={trip.length} departure={trip.departure} return={trip.return}/>
    ))
          return (
            <div className="App">
              <NavBar />
              <Hero />
              <br />
              <div className="container is-fluid">
                <FilterTrips />
                <div className="columns is-multiline">
                  {tripCards}
                </div>
                <Pagination />
                <div className="field">
                  <div className="control">
                    <input className="input" type="text" placeholder="Input" />
                  </div>
                </div>
              </div>
            </div>
          );
  }
}

export default App
