import React from"react";
import Strapi from "strapi-sdk-javascript/build/main";
import { setToken } from "../utils"
import PopUp from "../utils/PopUp"

const apiUrl = "https://strapi.luis-backt-und-kocht.de";
const strapi = new Strapi(apiUrl);

class Register extends React.Component {
    constructor() {
        super()

        this.state = {
           email: "",
           username:"",
           vorname: "",
           nachname:"",
           password:"",
           passwordRepeat:"",
           loading: false,
           popup: false,
           popupMessage:"sdfsdfs"
        }

        this.handleChange = this.handleChange.bind(this)
    }


    handleChange(event) {
        event.persist();
        const {name, value} = event.target

        this.setState({
            [name] : value,
                })

        console.log("ChangeState", this.state.username)
    }

    handleSubmit = async event => {
        event.preventDefault();
        this.setState({
            username: this.state.email
        })
        const { username, email, vorname, nachname, password} = this.state;
        console.log("SUBMIT", username, email, vorname, nachname, password)
        if (this.isFormEmpty(this.state)) {
            this.showPopUp("Bitte alle Felder ausfÃ¼llen!")
            return;
        }

        //Sign up User
        try {
            this.setState({loading:true})
            const response = await strapi.register( username, email, password)
            this.setState({ loading: false})
            setToken(response.jwt)
            //console..log("TOKEN", response.jwt)
            //this.redirectUser("/shopping-cart")
        } catch (err) {
            this.setState({loading: false})
            console.log("Register klappt nicht", err.message)
        }
    }

    isFormEmpty = ({ username, email, password }) => {
        return !username || !email || !password;
      };

      showPopUp = popupMessage => {
        this.setState({ popup: true, popupMessage });
        setTimeout(() => this.setState({ popup: false, popupMessage: "" }), 5000);
      };

    render() {
        const { popupMessage, popup, loading } = this.state;
    return(
        <div>
            <div className="box">
            <PopUp show={popup} message={popupMessage} />
            <p>
            <b>Sie haben noch kein Kundenkonto?</b>
            </p>
            <p className="is-size-7">Dann erÃ¶ffnen Sie einfach eines. FÃ¼llen Sie dazu bitte das folgende Formular aus.</p>
            <form className="has-text-left">
                    <div className="field">
                    <label className="label is-small has-text-left">Ihr Vorname</label>
                              <div className="control">
                                    <input 
                                        className="input is-primary is-small"
                                        type="vorname" 
                                        name="vorname" 
                                        id="vorname"
                                        placeholder="Vorname" 

                                        onChange={this.handleChange}
                                    />
                                </div>
                                </div>

                    <div className="field">
                    <label className="label is-small has-text-left">Ihr Nachname</label>
                                <div className="control">
                                    <input 
                                        className="input is-primary is-small"
                                        type="nachname" 
                                        name="nachname" 
                                        id="nachname"
                                        placeholder="Nachname" 
                                        onChange={this.handleChange}
                                    />
                                </div>
                                </div>
                    <div className="field">
                    <label className="label is-small has-text-left">Ihre EMail-Adresse</label>

                                <div className="control">
                                    <input 
                                        className="input is-primary is-small"
                                        type="email" 
                                        name="email" 
                                        id="email"
                                        placeholder="Ihre Email" 

                                        onChange={this.handleChange}
                                    />
                                </div>
                                </div>

                    <div className="field">
                    <label className="label is-small has-text-left">Ihr Passwort</label>

                                <div className="control">
                                    <input 
                                        className="input is-primary is-small"
                                        type="password" 
                                        name="password" 
                                        id="password"
                                        placeholder="password" 

                                        onChange={this.handleChange}
                                    />
                                </div>
                                </div>
                    <div className="field">
                    <label className="label is-small has-text-left">Bitte wiederholen Sie Ihr Passwort</label>

                                <div className="control">
                                    <input 
                                        className="input is-primary is-small"
                                        type="password" 
                                        name="passwordRepeat" 
                                        id="passwordRepeat"
                                        placeholder="passwordRepeat" 
                                        value={this.state.passwordRepeat}
                                        onChange={this.handleChange}
                                    />
                                </div>
                                </div>

                    <br />
                    <div className="has-text-centered">
                    <button className="button is-primary" onClick={this.handleSubmit}>Konto anlegen</button>
                    </div>


            </form>
            </div>
                <br/>

        </div>
    )
}
}

export default Register
