import React from"react"
import {Link} from "react-router-dom";

class Payment extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            loading: true
        }
    }

    componentDidMount(props) {
        if (this.props.location.state.user .vorname!= null) {
            this.setState({
                loading:false
            })
        }
    }

    render() {
        console.log("PAYMENT", this.props)
        return (
            <div className="container" style={{marginTop:"1em"}}>
                <div className="box">
                    <div className="title">Zahlung</div>
                    <div className="container">Ihre Daten
                        <div className="columns">
                            <div className="column">
                               <span className="has-text-weight-bold">Persönliche Daten</span>
                               <span className="is-size-6">
                                 {this.state.loading ? "LÄDT": "lädt nicht"}  </span> 
                            </div>
                            <div className="column">
                                Mitreisende
                            </div>
                        </div>
                    </div>
                    <div className="box has-background-primary has-text-white">
                        Ihre Buchung
                    </div>
                    <div className="box">Zahlungsarten</div>
                </div>
            </div>
        )
    }
}

export default Payment
