import React from "react"
import {FaBackspace} from "react-icons/fa"
import CartCustomer from"./CartCustomer"
import {setCart, getCart } from "../utils";

class ShoppingCart extends React.Component {
    constructor() {
        super();
        this.state = {
            booking: false,
            persist: false
        }
        this.handleBooking = this.handleBooking.bind(this)
    }

    componentWillMount() {
        this.setStategetCart()
        /*
        // console.log("Props", this.props)
        // console.log("MeinKontoKeys", localStorage.getItem("keys"))
        // console.log("MeinKonto", localStorage.getItem("username"))
        let localStorageKeys = []
         localStorageKeys.push(localStorage.getItem("keys").split(","))
         localStorageKeys.push(localStorage.getItem("cartKeys").split(","))
         //let temp = localStorageKeys.split(",")
        //console.log("LOCALSTORAGEKEYS", localStorageKeys)

        for (let o = 0; o < localStorageKeys.length; o++) {

         for (let i =0; i < localStorageKeys[o].length; i++) {
             console.log("Key für Storage", localStorageKeys[o][i])
            console.log("From Storagekeys", localStorage.getItem(localStorageKeys[o][i]))
             let key = localStorageKeys[o][i]
             let value = localStorage.getItem(localStorageKeys[o][i])
             this.setState({
                 [key] : value,
                persist: true
             })
         console.log("This.state", this.state)    
         }
     }
         // set the data in state and use it through the component
         //localStorage.clear();
         // removing the data from localStorage.  Since if user clicks for another invoice it overrides this data
      }

    componentDidMount() {
      //  if (this.statepersist) {console.log("Cart geladen")} else {
       let cartKeys = []
         //cartKeys.push(Object.keys(this.props.location.state.trip))
         // localStorage.setItem("cartKeys", cartKeys[0])
        for (const [key, value] of Object.entries(this.props.location.state.trip)) {

            let cart = "trip_"
            let concatKey = cart.concat(key)
            cartKeys.push(concatKey)
           // console.log("Für Local Cart", cartKeys)
            localStorage.setItem(concatKey, value);
          }
          for (const [key, value] of Object.entries(this.props.location.state)) {

            let cart = "trip_"
            let concatKey = cart.concat(key)
            cartKeys.push(concatKey)
           // console.log("Für Local Cart", cartKeys)
            localStorage.setItem(concatKey, value);
          }
          localStorage.setItem("cartKeys", cartKeys)
          let localStorageKeys = []
          localStorageKeys.push(localStorage.getItem("trip_id").split(","))
          //let temp = localStorageKeys.split(",")
         //console.log("LOCALSTORAGEKEYS_CART", localStorage.getItem("cartKeys"))
        // setCart(this.props.location.state) 
     //   }*/
    }

    handleBooking(event) {
        this.setState({
            booking: true

        })
        //console.log("Booking!", this.state.booking)
        document.getElementById("bookingButton").style.display ="none"
        document.getElementById("bookingAblauf").style.display ="block"

    }



    render(){
       // let tripId = this.state.trip.id
       // localStorage.setItem("tripId", "test")
       // console.log("LINK_PROPS", this.props.location.state.trip.id)
        var totalAdult = this.props.location.state.adults * this.props.location.state.trip.priceAdult
        const {kids, adults} = this.props.location.state
        const {priceAdult, priceReduced, title} = this.props.location.state.trip
        var totalKids = kids * priceReduced
        var grandTotal = totalKids + totalAdult
        const selectedTripDate = this.props.location.state.tripDate
        const dates = this.props.location.state.trip.trip_dates
     //   console.log("SelectedTripDate", selectedTripDate)
      //  console.log("Const Dates", dates)
        var bookedDate;
        for ( var i=0; i < dates.length; i++) {
            bookedDate =  dates.find(element => element.id === selectedTripDate)
        }
        var abreise = new Date(bookedDate.departure)
        var abreiseJahr = abreise.getFullYear()
        var abreiseMonat = abreise.getMonth()+1
        var abreiseTag = abreise.getDate()
        var    returnDatum = new Date(bookedDate.return)
        var    returnJahr = returnDatum.getFullYear()
        var    returnMonat = returnDatum.getMonth()+1
        var    returnTag = returnDatum.getDate()

     //   console.log("Finden", dates.find(element => element.id === selectedTripDate))
        return (

            <div className="container is-fluid">

                <div className="box">
                <div className="title">
                Ihre Reisebuchung
                </div>
                  <div className="columns">
                    <div className="column is-halfwidth">
                        <table className="table is-fullwidth is-striped">
                        <thead>
                        <tr>
                        <th>Reise</th>
                        <th>Abreise</th>
                        <th>Rückreise</th>
                        </tr>
                        </thead>
                        <tbody>
                        <tr>
                        <td>{title}</td>
                        <td>{abreiseTag}.{abreiseMonat}.{abreiseJahr}</td>
                        <td>{returnTag}.{returnMonat}.{returnJahr}</td>
                        </tr>
                        </tbody>
                        </table>
                        <div className="box"><b>Reisebeschreibung:</b> <br/> {this.props.location.state.trip.short_description}
                        </div>
                        <div style={{paddingTop:"1em"}}>
                            <div className="box is-hidden-mobile" id="bookingAblauf" style={{display:"none"}}>
                               <h3 className="has-text-weight-semibold is-family-secondary">Buchungsablauf</h3>
                               <ul className="is-size-7">
                                   <li>Melden Sie sich an oder legen Sie ein neues Kundenkonto an.</li>
                                    <li>Geben Sie die Daten Ihrer Mitreisenden an und prüfen Sie Ihre eigenen Daten</li>
