import React from "react"
import {setCart, getCart } from "../utils";
import Cookie from "js-cookie";
import Strapi from "strapi-sdk-javascript/build/main"

class AccountAdress extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            items: [],
            loading: false,
            token: ""

        }
    }

   async componentWillMount() {
       await this.setState({
            items: getCart(),
            token: Cookie.get('jwt')

       }) 
   }  

   async handleUpdate() {
       this.setState({
           loading: true
       })
       if (this.state.token !== null) {
        // authenticate the token on the server and place set user object
        fetch('https://strapi.luis-backt-und-kocht.de/users/me', {
          headers: {
            Authorization: `Bearer ${this.state.token}`,
          },
        }).then(async (res) => {
            const userData = await res.json()
            console.log("ANtwor for MEE", userData)
            await this.setState({
                user: userData,

            })
            console.log("USERDATASTATE", this.state)

            this.setState({
                loading:false
            })

})}

   }


    render() {
        console.log("GetCartItems", this.props)
        let keys = this.props.keys
        let data = this.state.data
        console.log("DATA", this.props)
        if (this.state.data !== "") {
        console.log("Whats the state", this.state)
        }
        for (const [key, value] of Object.entries(this.props.data)) {
            //console.log(`${key}: ${value}`);
            if (this.state.hasOwnProperty(key)) {
            //    console.log("SChon da")
            }
            else {
            //    console.log("NEUUUUUUU", this.state[key])

            this.setState ({
               [key]:value
       })
            }
        }

       //console.log("AccountSTate", this.state)
        return (
            <div className="container">
                                <form>
                                <div className="field">
                                    <label className="label is-small has-text-left">Anrede</label>
                                    <div className="control">
                                     <input className="input is-small is-primary" type="text" placeholder="Anrede" value={this.state.anrede} />
                                    </div>
                                </div>
                                <div className="columns">
                                    <div className="column">
                                        <div className="field">
                                            <label className="label is-small has-text-left">Vorname</label>
                                            <div className="control">
                                            <input className="input is-small is-primary" type="text" placeholder="Vorname" value={this.state.vorname} />
                                        </div>
                                    </div>
                                </div>
                                <div className="column">
                                    <div className="field">
                                        <label className="label is-small has-text-left">Nachname</label>
                                        <div className="control">
                                        <input className="input is-small is-primary" type="text" placeholder="Nachname" value={this.state.nachname} />
                                    </div>
                                </div>
                                </div>
                                </div>
                                <div className="columns">
                                    <div className="field column is-three-quarters">
                                        <label className="label is-small has-text-left">Strasse</label>
                                        <div className="control">
                                        <input className="input is-small is-primary" type="text" placeholder="Strasse" value={this.state.Strasse} />
                                        </div>
                                    </div>
                                    <div className="field column">
                                        <label className="label is-small has-text-left">Hausnummer</label>
                                        <div className="control">
                                        <input className="input is-small is-primary" type="text" placeholder="Hausnummer" value={this.state.Hausnummer}/>
                                        </div>
                                    </div>
                                </div>
                                <div className="columns">
                                    <div className="field column">
                                        <label className="label is-small has-text-left">Postleitzahl</label>
                                        <div className="control">
                                        <input className="input is-small is-primary" type="text" placeholder="Postleitzahl" value={this.state.plz}/>
                                        </div>
                                    </div>
                                    <div className="field column is-three-quarters">
                                        <label className="label is-small has-text-left">Ort</label>
                                        <div className="control">
                                        <input className="input is-small is-primary" type="text" placeholder="Ort" value={this.state.ort}/>
                                        </div>
                                    </div>
                                </div>
                                <div className="columns">
                                    <div className="field column">
                                        <label className="label is-small has-text-left">Telefon</label>
                                        <div className="control">
                                        <input className="input is-small is-primary" type="text" placeholder="Telefon" value={this.state.telefon} />
                                        </div>
                                        <label className="label is-small has-text-left">Mobil</label>
                                        <div className="control">
                                        <input className="input is-small is-primary" type="text" placeholder="mobil" value={this.state.mobil}/>
                                        </div>
                                    </div>
                                    <div className="field column">
                                        <label className="label is-small has-text-left">Email</label>
                                        <div className="control">
                                        <input className="input is-small is-primary" type="email" placeholder="Email" value={this.state.email} />
                                        </div>
                                    </div>
                                </div>
                                <div className="field is-grouped">
                                        <div className="control">
                                        <button className="button is-primary">Ã„ndern</button>
                                        </div>
                                    </div>
                                </form>
                            </div>
        )
    }
}

export default AccountAdress
