import React from "react"
import { FaBusAlt, FaBed, FaStar } from "react-icons/fa";
//import { checkServerIdentity } from "tls";
import {GiBus, GiPayMoney, GiKnifeFork} from "react-icons/gi"
import { FaAngleDown } from "react-icons/fa";
import DetailPrice from "./DetailPrice"

class DetailServices extends React.Component {
    constructor() {
        super()
        this.state = {
            loading: true,
            services: [], 
            adults:0,
            kids: 0, 
            tripPrice: 0, 
            showPrice: false
        }
        this.handleChange = this.handleChange.bind(this)
        //this.showPrice = this.showPrice.bind(this)
    }

    componentDidMount(props) {
        console.log("DetailServices Mounted", this.props.priceAdult)
        this.setState({
            loading:false,
            services: this.props.services
        })

    }


      handleChange(event) {
        const {name, value} = event.target
        this.setState({ 
            [name]: value,
           showPrice: true
         })

      }



   render() {
    var tool = 'data-tooltip="Über Variable"';
   var icon
    var serviceICON = this.state.services.map(service => {
        console.log("ServiceID", service.serviceName)
        switch(service.id) {
            case "1": 
                icon = <tr><td><GiKnifeFork /></td><td>  {service.serviceName}</td></tr>   ;
                break;
                case "2": 
                icon = <tr><td><FaBusAlt /></td><td>Busanreise</td></tr>;
                break;
             case "3": 
            icon = <tr><td><GiBus /> </td><td> {service.serviceName}</td></tr>  ;
            break;
            case "4": 
            icon = <tr><td> <FaBed /> </td><td> {service.serviceName}</td></tr>  ;
            break;
            case "5": 
            icon = <tr><td><FaStar />  </td><td> {service.serviceName}</td></tr>  ;
            break;
            case "6": 
            icon = <tr><td><GiPayMoney />  </td><td> {service.serviceName}</td></tr>  ;
            break;

        }
        return icon;
    });



    this.state.showPrice ? console.log("Formular", this.state.tripPrice) : console.log("Nix zu sehen")

    return (
        <div>
        <div className="box">
        <div class="field">

                <div class="control is-expanded">
                 <div class="select is-primary is-fullwidth">
                     <select>
                     <option>Bitte wählen Sie Ihr Reisedatum</option>
                     <option>1</option>
                     <option>2</option>
                     <option>3</option>
                     <option>4</option>
                     <option>5</option>

                     </select>
                  </div>
                 </div>
            </div>



        <div className="columns">
        <div className="column is-half-width">
            <div class="field">

                <div class="control is-expanded">
                 <div class="select is-small is-primary is-fullwidth">
                     <select 
                            name="adults"
                            value={this.state.adults}
                            onChange={this.handleChange}
                            >
                     <option>Erwachsene</option>
                     <option value="1">1</option>
                     <option value="2">2</option>
                     <option value="3">3</option>
                     <option value="4">4</option>
                     <option value="5">5</option>

                     </select>
                  </div>
                 </div>
            </div>
        </div>
        <div className="column is-half-width">
            <div class="field">

                <div class="control is-expanded">
                 <div class="select is-small is-primary is-fullwidth">
                     <select name="kids"
                            value={this.state.kids}
                            onChange={this.handleChange}>
                     <option>Kinder</option>
                     <option value="1">1</option>
                     <option value="2">2</option>
                     <option value="3">3</option>
                     <option value="4">4</option>
                     <option value="5">5</option>
                     </select>
                  </div>
                 </div>
        </div>
         </div>
         </div>
        {this.state.showPrice ? <DetailPrice adults={this.state.adults} adultPrice={this.props.priceAdult} kids={this.state.kids} reducedPrice={this.props.priceReduced} /> : console.log("Nix")}
        <div>
          </div>
          </div>
        <div className="box">
            Ihre Inklusivleistungen
            <table className="table is-striped is-size-7 is-fullwidth">
            <tbody>
            {serviceICON}
            </tbody>
            </table>
            </div>


       </div>

    )         
   }
}


export default DetailServices
