import React from"react"
import {Link} from "react-router-dom";

class Payment extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            loading: true
        }
    }

    componentDidMount(props) {
        if (this.props.location.state != null) {
            this.setState({
                loading:false
            })
        }

    }

    render() {
        console.log("PAYMENT", this.props)
        return (
            <div className="container" style={{marginTop:"1em"}}>
                <div className="box">
                    <div className="title">Zahlung</div>
                    <div className="container">Ihre Daten
                        <div className="columns">
                            <div className="column has-text-left">
                                <div className="box is-shadowless">
                               <span className="has-text-weight-bold">Ihre Anschrift</span><br/><br/>
                               <span className="is-size-6">

                                 {this.state.loading ? "LÄDT": 
                                 <p>
                                 {this.props.location.state.booking.user.vorname} {this.props.location.state.booking.user.nachname}<br/>
                                 {this.props.location.state.booking.user.Strasse} {this.props.location.state.booking.user.Hausnummer}
                                 {this.props.location.state.booking.user.plz} {this.props.location.state.booking.user.ort} 
                                  </p>} 

                                <p>
                                    <b>Kontaktdaten</b><br />
                                    {this.props.location.state.booking.user.telefon}
                                    {this.props.location.state.booking.user.mobil}
                                    {this.props.location.state.booking.user.email}</p> 


                                 </span> 
                                 </div>
                            </div>
                            <div className="column">
                                Mitreisende
                            </div>
                        </div>
                    </div>
                    <div className="box has-background-primary has-text-white">
                        Ihre Buchung
                    </div>
                    <div className="box">Zahlungsarten</div>
                </div>
            </div>
        )
    }
}

export default Payment
