import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';

class DetailHeroCarousel extends Component {
     constructor() {   
        super()
        this.state = {
            loading:true,
            pics: []
        }
    }

    componentDidMount(props)  {
        this.setState({
            loading:false,
            pics: this.props.pics
        })
    }


    render() {
        console.log("Carousel1", props.pic[0])
        this.state.loading ? console.log("State Loading") : console.log("State done", this.state)
        return (
            <Carousel axis="vertical" useKeyboardArrows autoPlay infiniteLoop>
                <div className="carouselHeight">
                    <img src="http://placehold.it/350x150" width="350px" height="150px" />
                        <p className="legend">
                        Legend 
                        </p>
                </div>
                <div>
                    <img src="http://placehold.it/350x150" />

                </div>
                <div>
                    <img src="http://placehold.it/350x150" />

                </div>
            </Carousel>
        );
    }
}

export default DetailHeroCarousel
