import React from"react";

class Register extends React.Component {
    state = {
        anrede:'',
        vorname:'',
        nachname:'',
        email:'',
        passsword:''
    }

    handleChange(event) {
        const {name, value} = event.target
        console.log("EVENT", name, value)
        //this.setState({
        //    [name] : value
        //})
    }

    render() {
    return(
        <div>
            <div className="box">
            <p>
            <b>Sie haben noch kein Kundenkonto?</b>
            </p>
            <p className="is-size-7">Dann eröffnen Sie einfach eines. Füllen Sie dazu bitte das folgende Formular aus.</p>
            <form className="has-text-left">
                              <div className="control">
                                    <input 
                                        className="input is-primary is-small"
                                        type="text" 
                                        name="vorname" 
                                        id="vorname"
                                        placeholder="login" 
                                        value={this.state.login}
                                        onChange={this.handleChange}
                                    />
                                </div>
                                <div className="control">
                                    <input 
                                        className="input is-primary is-small"
                                        type="email" 
                                        name="login" 
                                        id="login"
                                        placeholder="login" 
                                        value={this.state.login}
                                        onChange={this.handleChange}
                                    />
                                </div>

                    <br />
                    <div className="has-text-centered">
                    <button className="button is-primary">Konto anlegen</button>
                    </div>


            </form>
            </div>
                <br/>
        </div>
    )
}
}

export default Register
