import React from "react";
import {useParams} from "react-router-dom";
import Strapi from "strapi-sdk-javascript/build/main"
import {withRouter} from "react-router"
import DetailHeroCarousel from "./DetailHeroCarousel"

    const apiUrl= 'http://home.luis-backt-und-kocht.de:1337';
    //const apiUrl = `${process.env.REACT_APP_BACKEND_URL}`
    const strapi = new Strapi(apiUrl)

class TripsDetail extends React.Component {
        constructor() {
            super()
            this.state= {
            trip:[],
            loading: true
            }
        }

       async componentDidMount() {
    try{
        const response = await strapi.request('POST', '/graphql', {
          data: {
            query: `query { trip (id: "${this.props.match.params.tripId}") {
                title
                id
  trip_dates {
    departure
    return
  }
  short_description
  long_description
  pics {
    name
    url
  }
  priceAdult
  priceReduced

}}`
          }

        });
        console.log("responseDdtail",response.data)
        this.setState({ 
          trip: response.data.trip,
          loading: false})
    } catch(err) {
      console.error(err) };
    }

    render() {
        console.log("DetailState", this.state.trip)
        return ( 
            <div>
        <div className="hero"> 

        <div classNAme="hero-body">
        <DetailHeroCarousel />
        </div>
        <h1 className="
                is-size-1 
                has-text-black
                has-background-white 
                has-text-left 
                rounded 
                is-padded-10">
                <strong>{this.state.trip.title}</strong></h1>
        </div>

            </div>

            )  
    }

}

export default withRouter(TripsDetail)
